import React from 'react';
import SectionHeading from './SectionHeading';

export default function Experience({ data }) {
  const { sectionHeading, allExperience } = data;
  // const { sectionHeading } = hardSkills; // Assuming both use same heading

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };
  return (
    <section className="section gray-bg">
      <div className="container">
        <SectionHeading
          miniTitle={sectionHeading.miniTitle}
          title={sectionHeading.title}
        />
        <h3>Hard Skills</h3>
        <Slider {...settings}>
          {hardSkills.allExperience?.map((item, index) => (
            <div className="ex-box" key={index} data-aos="fade-up" data-aos-duration="1200" data-aos-delay={index * 100}>
              <h4>{item.designation}</h4>
              <label>{item.jobType}</label>
            </div>
          ))}
        </Slider>

        <h3>Soft Skills</h3>
        <Slider {...settings}>
          {softSkills.allExperience?.map((item, index) => (
            <div className="ex-box" key={index} data-aos="fade-up" data-aos-duration="1200" data-aos-delay={index * 100}>
              <h4>{item.designation}</h4>
              <label>{item.jobType}</label>
            </div>
          ))}
        </Slider>
      </div>
    </section>
  );
}
